import React, { Component } from "react";
import Swap from "./abis/Swap.json";
import detectEthereumProvider from '@metamask/detect-provider';
import Web3 from 'web3';
import { Grid, Button, Form, Input, Label, Card, Header } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';


import "./App.css";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      account: '',
      contract: null,
      addressToken1: "",
      addressToken2: "",
      address: "",
      amount: 0
    }
  }

  async componentDidMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  // first up is to detect ethereum provider
  async loadWeb3() {
    const provider = await detectEthereumProvider();

    // modern browsers
    if (provider) {
      console.log('Ethereum wallet is connected');

      window.web3 = new Web3(provider);
    } else {
      console.log('No ethereum wallet detected');
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3;
    const accounts = await window.web3.eth.getAccounts();
    this.setState({ account: accounts[0] })

    const networkId = await web3.eth.net.getId();
    const networkData = Swap.networks[networkId];
    if (networkData) {
      const abi = Swap.abi;
      const address = networkData.address;
      const contract = new web3.eth.Contract(abi, address);
      this.setState({ contract });
    } else {
      window.alert('Smart contract not deployed!')
    }

  }

  handleInputChange = (event) => {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    })
  }

  getQuote = () => {
    let { address, amount } = this.state;
    //await this.state.contract.methods.getExchangeRate(amount, address);
  }

  render() {
    return (
      <div className="App">
        <br></br>
        <Header as='h1'>Create a 50/50 Crypto Portfolio</Header>
        <form>
        <Input type="text" placeholder="TokenAddress" name="address" onChange={this.handleInputChange}></Input>
        <Input type="text" placeholder="Value" name="amount" onChange={this.handleInputChange}></Input>
        <Button type="submit" onClick={this.getQuote} style={{ margin: "20px" }}>Get exchange rate</Button>
        </form>
        {this.state.address}
      </div >
    );
  }
}

export default App;
